import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { login } from '../../actions/auth';
import axios from 'axios';
import { Form } from 'react-bootstrap';
const Login = ({ login, isAuthenticated }) => {
    const [formData, setFormData] = useState({
        email: '',
        password: '' 
    });

    const { email, password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();

        login(email, password);
    };

    const continueWithGoogle = async () => {
        try {
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/auth/o/google-oauth2/?redirect_uri=${process.env.REACT_APP_API_URL}/google`)

            window.location.replace(res.data.authorization_url);
        } catch (err) {

        }
    };



    if (isAuthenticated) {
        return <Redirect to='/' />
    }

    return (
        <div>
        <div className="col d-flex justify-content-center">
          <div className="row w-100 mx-0">
            <div className="col-lg-7 mx-auto">
              <div className="auth-form-light text-left py-5 px-4 px-sm-5">
                <div className="brand-logo">
                  <img src='https://cdn.dribbble.com/users/78433/screenshots/10905315/gmail-dribbble_4x.png' alt="logo" />
                </div>
                <h4>Hello! let's get started</h4>
                <h6 className="font-weight-light">Sign in to continue.</h6>
                <Form className="pt-3"  onSubmit={e => onSubmit(e)}>
                  <Form.Group className="d-flex search-field">
                    <Form.Control type="email" placeholder="Email..." size="lg" className="h-auto"  name='email'
                        value={email}
                        onChange={e => onChange(e)}
                        required />
                  </Form.Group>
                  <Form.Group className="d-flex search-field">
                    <Form.Control type="password" placeholder="Password" size="lg" className="h-auto"  name='password'
                        value={password}
                        onChange={e => onChange(e)}
                        minLength='6'
                        required/>
                  </Form.Group>
                  <div className="mt-3">
                    <button className="btn btn-block btn-primary btn-lg font-weight-medium auth-form-btn" type='submit'>SIGN IN</button>
                  </div>



                </Form>
                <div className="mb-2">
                    <button type="button" className="btn btn-block btn-gmail auth-form-btn" onClick={continueWithGoogle}>
                      <i className="mdi mdi-gmail mr-2"></i>Connect using Gmail
                    </button>
                  </div>
              </div>

            </div>
          </div>
        </div>  
      </div>
    );
};

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { login })(Login);
