import axios from 'axios';
import { reset } from 'redux-form';
import history from '../history';
import { tokenHeader } from '../headersauth';
import { GET_DATAS, GET_DATA, ADD_DATA, DELETE_DATA, EDIT_DATA } from './types';

// GET DATAS
export const getDATAS = () => async (dispatch, getState) => {
  const res = await axios.get('/api/project/', tokenHeader(getState));
  dispatch({
    type: GET_DATAS,
    payload: res.data
  });
};

// GET DATA
export const getDATA = id => async (dispatch, getState) => {
  const res = await axios.get(`/api/project/${id}/`, tokenHeader(getState));
  dispatch({
    type: GET_DATA,
    payload: res.data
  });
};

// ADD DATA
export const addDATA = formValues => async (dispatch, getState) => {
  const res = await axios.post(
    '/api/project/',
    { ...formValues },
    tokenHeader(getState)
  );
  dispatch({
    type: ADD_DATA,
    payload: res.data
  });
  dispatch(reset('DATAForm')); 
};

// DELETE DATA
export const deleteDATA = id => async (dispatch, getState) => {
  await axios.delete(`/api/project/${id}/`, tokenHeader(getState));
  dispatch({
    type: DELETE_DATA,
    payload: id
  });
  history.push('/');
};

// EDIT DATA
export const editDATA = (id, formValues) => async (dispatch, getState) => {
  const res = await axios.patch(
    `/api/project/${id}/`,
    formValues,
    tokenHeader(getState)
  );
  dispatch({
    type: EDIT_DATA,
    payload: res.data
  });
  history.push('/');
};
